import React, { useState } from 'react';
import { useDispatch,useSelector } from 'react-redux';
import { useEffect } from 'react';
import { getNews } from './sagas/Newreducer';
import { Card, Dropdown,Skeleton,Input } from 'antd';
import 'antd/dist/antd.css';
import useDounce from './useDebounce';
const Ant = () => {
    const dispatch=useDispatch()
    const [loading,setLoading]=useState(false)
    const {}=In
    const showSkeleton=(()=>{
        setLoading(true)
        setTimeout(()=>{
            setLoading(false)
        },3000)
    })
  useEffect(()=>{
    dispatch(getNews())
    showSkeleton()
  },[dispatch])

  const hits=useSelector((state)=> state.news.hits)
  const handleChange=()=>{}
  console.log("hahaha",hits)
    return (
        <>
          <Search
      placeholder="input search text"
      enterButton="Search"
      size="large"
      onSearch={handleChange}
    />
        <Skeleton loading={loading}>
     <div style={{"display":"flex","flexWrap":"wrap","gap":"30px","padding":"50px"}}>
        {hits.length > 0 && hits.map(item => (
            <Card title="Card title"   hoverable
    style={{
      width: 240,
    }} key={item.id} >
                {item.title}
            </Card>
        ))}
     </div>
        </Skeleton>
        </>
    );
};

export default Ant;
