import { getNews } from './sagas/Newreducer';
import { Card, Dropdown } from 'antd';
import 'antd/dist/antd.css';
import useDounce from './useDebounce';
const Ant = () => {
    const dispatch=useDispatch()
    const [loading,setLoading]=useState(false)
    const [loadItem,setLoadItem]=useState('')
    const filterDebounce=useDounce(loading,500)
    const showSkeleton=(()=>{
        setLoading(true)
    })
  useEffect(()=>{
    dispatch(getNews())
  },[dispatch,filterDebounce])

  const hits=useSelector((state)=> state.news.hits)

  console.log("hahaha",hits)
    return (
     <div style={{"display":"flex","flexWrap":"wrap","gap":"30px","padding":"50px"}}>
        {hits.length > 0 && hits.map(item => (
            <Card title="Card title"   hoverable
    style={{
      width: 240,
    }} key={item.id} >
                {item.title}
            </Card>
        ))}
     </div>
    );
};

export default Ant;
