import React, { useEffect } from 'react';
import 'antd/dist/antd.css';
import { useSelector,useDispatch } from 'react-redux';
import { handleFetchNews } from './newSlice';
const Ant_Thunk = () => {
     const {results,loading,query}=useSelector(state=>state.movie)
     const dispatch=useDispatch()

     useEffect(()=>{
        dispatch(handleFetchNews())
     })
    return (
        <div>
            <Search
          style={{width:500,marginLeft:500,marginTop:50}}
      placeholder="input search text"
      enterButton="Search"
      size="large"
      onChange={handleChangeQuery}
    />
        <Skeleton loading={loading}>
     <div style={{"display":"flex","flexWrap":"wrap","gap":"30px","padding":"50px"}}>
        {results.length > 0 && results.map(item => (
            <Card title="Card title"   hoverable
    style={{
      width: 240,
    }} key={item.id} >
                {item.title}
            </Card>
        ))}
     </div>
        </Skeleton>
        </div>
    );
};

export default Ant_Thunk;
